package com.javaex.practice03;

public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<5;i++) {
			System.out.println(i);
		}
		System.out.println("========================");
		
		for(int i=10; i>0; i=i-2) {
			System.out.println(i);
		}
		
		
		
		
		
//		i는 1 2 3 4 4가지 수를 갖는다
//		======================================
//		i는 10에서 2씩 빠지는 숫자여서 10 8 6 4 2 이 된다
	}

}
